import React, { createContext, useState, useContext, useEffect } from 'react';

// 创建主题上下文
const ThemeContext = createContext();

// 主题配置
export const themes = {
  light: {
    name: 'light',
    colors: {
      background: '#ffffff',
      headerBackground: '#f5f5f5',
      text: '#282c34',
      textSecondary: '#8899a6',
      textTertiary: '#a0a0a0',
      link: '#61dafb',
    }
  },
  dark: {
    name: 'dark',
    colors: {
      background: '#282c34',
      headerBackground: '#282c34',
      text: '#ffffff',
      textSecondary: '#8899a6',
      textTertiary: '#a0a0a0',
      link: '#61dafb',
    }
  }
};

// 主题 Provider 组件
export const ThemeProvider = ({ children }) => {
  // 从 localStorage 读取主题，默认为 light
  const [theme, setTheme] = useState(() => {
    const savedTheme = localStorage.getItem('theme');
    return savedTheme || 'light';
  });

  // 切换主题
  const toggleTheme = () => {
    setTheme(prevTheme => {
      const newTheme = prevTheme === 'light' ? 'dark' : 'light';
      localStorage.setItem('theme', newTheme);
      return newTheme;
    });
  };

  // 应用主题到 document
  useEffect(() => {
    const themeData = themes[theme];
    const root = document.documentElement;

    // 设置 CSS 变量
    root.style.setProperty('--background-color', themeData.colors.background);
    root.style.setProperty('--header-background', themeData.colors.headerBackground);
    root.style.setProperty('--text-color', themeData.colors.text);
    root.style.setProperty('--text-secondary', themeData.colors.textSecondary);
    root.style.setProperty('--text-tertiary', themeData.colors.textTertiary);
    root.style.setProperty('--link-color', themeData.colors.link);

    // 设置 data-theme 属性
    root.setAttribute('data-theme', theme);
  }, [theme]);

  return (
    <ThemeContext.Provider value={{ theme, toggleTheme }}>
      {children}
    </ThemeContext.Provider>
  );
};

// 自定义 hook 用于使用主题
export const useTheme = () => {
  const context = useContext(ThemeContext);
  if (!context) {
    throw new Error('useTheme must be used within a ThemeProvider');
  }
  return context;
};

export default ThemeContext;
